import Header from "./Header"
import Content from "./Content"
import Footer from "./Footer"
import React, { useState } from 'react'
import AddItem from "./AddItem";




function App() {

    const [items,setItems]= useState(
      [
        {
          id:1, 
          checked:false,
          iteM: "Practice Coding"
        },
        {
          id:2, 
          checked:false,
          iteM: "Play Cricket"
        },
        {
          id:3, 
          checked:true,
          iteM: "Read About AI"
        }
      ]);


        const [newItem,setNewItem]=useState('') // Type pandra word aa oru varaible la vangaradhu ku use <pandrathu styleName=""></pandrathu>

        const addItem = (item) =>{
          const id= items.length ? items[items.length-1].id+1 : 1;

          const addNewItem={id,checked:false,item}
          const listItems=[...item,addNewItem]
          setItems(listItems)
          localStorage.setItem("todo_list", JSON.stringify(listItems))
        }


        const handleCheck =(ID)=>{
          const listItems= items.map((item)=>item.id===ID ? {...item, checked:!item.checked} : item)
          setItems(listItems)
          localStorage.setItem("todo_list", JSON.stringify(listItems))
        }

        const handleDelete =(ID)=>{
          const listItems= items.filter((item)=>item.id!==ID)
          setItems(listItems)
          localStorage.setItem("todo_list", JSON.stringify(listItems))    // THIS "setItem is different..."

        }

        const handleSubmit=(e)=>{
          e.preventDefault();
          // console.log("Submitted")
          if(!newItem) return;
          console.log(newItem)
          //Add
          setNewItem('')    //Comment this to see the changes....

        }
  return (
    <div className="App">
      <Header title='Pravin Velu' /> 
      <AddItem 
        newItem={newItem}
        setNewItem={setNewItem}
        handleSubmit={handleSubmit}
        />

      <Content 
        items= {items}
        handleCheck={handleCheck}
        handleDelete={handleDelete}
      />
      <Footer
        lengtH = {items.length}/>
    </div>
  )

  }
export default App;


