import Header from "./Header"
import Content from "./Content"
import Footer from "./Footer"
import React, { useState } from 'react'
import AddItem from "./AddItem"
import SearchItem from "./SearchItem"




function App() {

    const [items,setItems]= useState(JSON.parse(localStorage.getItem("todo_list")));




        const [newItem,setNewItem]=useState('') // Type pandra word aa oru varaible la vangaradhu ku use pandrathu.......

        const [search, setsearch]=useState("")

        const addItem = (iteM) =>{
          const id= items.length ? items[items.length-1].id +1 : 1;

          const addNewItem={id,checked:false,iteM}
          const listItems=[...items,addNewItem]
          setItems(listItems)
          localStorage.setItem("todo_list", JSON.stringify(listItems))
        }


        const handleCheck =(ID)=>{
          const listItems= items.map((item)=>item.id===ID ? {...item, checked:!item.checked} : item)
          setItems(listItems)
          localStorage.setItem("todo_list", JSON.stringify(listItems))
        }

        const handleDelete =(ID)=>{
          const listItems= items.filter((item)=>item.id!==ID)
          setItems(listItems)
          localStorage.setItem("todo_list", JSON.stringify(listItems))    // THIS "setItem is different..."

        }

        const handleSubmit=(e)=>{
          e.preventDefault();
          // console.log("Submitted")
          if(!newItem) return;
          console.log(newItem)
          //Add
          addItem(newItem)
          setNewItem('')    //Comment this to see the changes....

        }
  return (
    <div className="App">
      <Header title='To - Do List ' /> 
      <AddItem 
        newItem={newItem}
        setNewItem={setNewItem}
        handleSubmit={handleSubmit}
        />
      <SearchItem
        search={search}
        setSearch={setsearch}
      />

      <Content 
        items= {items.filter(item=>(item.iteM).toLowercase().includes(search.toLocaleLowerCase))}
        handleCheck={handleCheck}
        handleDelete={handleDelete}
      />
      <Footer
        lengtH = {items.length}/>
    </div>
  )

  }
export default App;


